%Aufgabe2.2b

float_plus(F1,F2,Result):-
Result is F1+F2.

%gesamtstromverbrauch(?Total)
gesamtstromverbrauch(Total):-
findall(Stamps,energy_time_series(_,_,_,Stamps),List),
matrixtransform(List,TList),
findall(Result,
		(nth0(X,TList,Elem),
			foldl(float_plus,Elem, 0, Result)),
		Total).

