%Erzeugt eine energy time series mit dem Gesamtverbrauch
ets('Gesamt', energy_time_series('Gesamt', TimeStamp, Granularity, L)) :-
    energy_time_series('Export', TimeStamp, Granularity, _),
    gesamtstromverbrauch(L).

%Aufgabe 2.3
%a)
correlate(energy_time_series(_,_,_,S1), energy_time_series(_,_,_,S2), C) :-
    correlate_pearson(S1, S2, C).
%?- correlate(energy_time_series('A',0,0,[1,2]),energy_time_series('A',0,0,[1,2]),X).
%X = 1.0.
%?- correlate(energy_time_series('A',0,0,[1,2]),energy_time_series('A',0,0,[2,-1]),X).
%X = -1.0.

%Berechnet den Pearson’schen Korrelationskoeffizient zwischen 2 Vektoren
correlate_pearson(V1, V2, C) :-
    length(V1,N),
    length(V2,N),
    produktsumme(V1, V2, PS),
    summenprodukt(V1, V2, SP),
    nenner(V1, NX),
    nenner(V2, NY),
    C is (N * PS - SP) / (NX * NY).
%?- correlate_pearson([1,2], [1,2], X).
%X = 1.0.
%?- correlate_pearson([1,2], [2,-1], X).
%X = -1.0.

%Summe der Produkte beider Listen
produktsumme([],[], 0).
produktsumme([H1|T1], [H2|T2], X) :-
    length(T1,L),
    length(T2,L),
    produktsumme(T1, T2, Sum),
    X is (H1 * H2) + Sum.
%?- produktsumme([1,2,1],[1,2,3],X).
%X = 8.

%Produkt der Summe beider Listen
%summenprodukt([H1|[]],[H2|[]],X) :-
%    X is H1 * H2.
%summenprodukt([H11,H12|T1],[H21,H22|T2],X) :-
%    length(T1,L),
%    length(T2,L),
%    HN1 is H11 + H12,
%    HN2 is H21 + H22,
%    append([HN1], T1, L1),
%    append([HN2], T2, L2),
%    summenprodukt(L1, L2, X).
summenprodukt(L1, L2, X):-
    list_summe(L1,S1),
    list_summe(L2,S2),
    X is S1 * S2.
%?- summenprodukt([2,1],[3,1],X).
%X = 12 .

nenner(L, X):-
    length(L,N),
    list_summe(L, S),
    list_summe_quadrat(L, SQ),
    X is (N * SQ - (S * S)) ** 0.5.
%für [2,3]: N = 2, S = 5, SQ = 13
%X = (2*13 - 5*5) ** 0.5 = (26-25)**0.5 = 1
%?- nenner([2,3],X).
%X = 1.0.

%Summe der Elemente einer Liste
list_summe([], 0).
list_summe([H|T], Sum) :-
    list_summe(T, SumRest),
    Sum is H + SumRest.
%?- list_summe([1,2,3],X).
%X = 6.

%Summe der Quadrate der Elemente einer Liste
list_summe_quadrat([], 0).
list_summe_quadrat([H|T], Sum) :-
    list_summe_quadrat(T, SumRest),
    Sum is (H * H) + SumRest.
%?- list_summe_quadrat([1,2,3],X).
%X = 14.

